INSERT INTO `question` VALUES (3, '统计学', '<p id=\"descriptionP\"><p><span style=\"color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 2.25em; font-weight: bold; white-space: pre-wrap;\">统计学</span></p></p><h2 cid=\"n2\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">1. Introduction</span></h2><p cid=\"n3\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><span md-inline=\"strong\"><strong><span md-inline=\"plain\">Descriptive</span></strong></span></p><ul cid=\"n4\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n5\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n6\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">Organize and summarize the data</span></p></li><li cid=\"n7\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n8\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-