-- ----------------------------
-- Table structure for question
-- ----------------------------
DROP TABLE IF EXISTS `question`;
CREATE TABLE `question`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '内容',
  `gmt_create` bigint(20) NOT NULL COMMENT '发布时间',
  `gmt_modified` bigint(20) NOT NULL COMMENT ' 修改时间',
  `creator` bigint(20) NOT NULL COMMENT ' 文章作者id user_id',
  `comment_count` int(11) NOT NULL DEFAULT 0 COMMENT '评论数量',
  `view_count` int(11) NOT NULL DEFAULT 0 COMMENT ' 阅读数量',
  `like_count` int(11) NOT NULL DEFAULT 0 COMMENT '点赞数量',
  `tag` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章标签, 一个文章可以有多个标签, 标签之间用英文逗号隔开',
  `gmt_latest_comment` bigint(20) NOT NULL COMMENT '最新评论的时间',
  `status` int(11) NOT NULL DEFAULT 0 COMMENT '1加精，2置顶，3精+顶',
  `column2` int(3) NULL DEFAULT 2 COMMENT '专栏号',
  `permission` int(3) NULL DEFAULT 0 COMMENT '阅读权限',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 9 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

