INSERT INTO `question` VALUES (8, '阅读《见识》笔记', '<h2 cid=\"n2\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">1.幸福是目的，成功是手段</span></h2><h3 cid=\"n3\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">人生是一条河</span></h3><ul cid=\"n4\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n5\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n6\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">幸福感的本源只有两个--基因的传承和影响力</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">基因的传承：繁衍   影响力：社会地位</span></p></li><li cid=\"n7\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n8\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">任何社会都是分层次的</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">我们现在要做的就是努力提高层次</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">要相信自己，只有有信心，有意识地锻炼自己，才能打破社会层级，只有更加努力，更加自信，才能走到顶层。</span></p></li></ul><h3 cid=\"n9\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">人生最重要的投资</span></h3><ul cid=\"n10\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n11\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n12\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">男生应该有足够的独立性，并且承担对未来自己未来家庭的义务。</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">要承担属于自己的责任，同事要提高自己的能力，使得自己用于承担责任的能力。</span></p></li><li cid=\"n13\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n14\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">恋爱是激情，是化学物质所带来的愉悦；婚姻则是由两个人一同构造一个舒适的共同体，在那个共同体中，双方都将受益。 </span></p></li><li cid=\"n15\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n16\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">婚姻观：夫妻间关系的重要性高于其他关系的重要性</span></p></li></ul><h3 cid=\"n17\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">先让父母成熟起来</span></h3><ul cid=\"n18\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n19\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n20\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">父母成熟，首先要自己不断进步</span></p></li><li cid=\"n21\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n22\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">把自己看成一个成年人，用一种尊敬，而且是成年人之间的沟通方式和长辈说话，要不断将自己接受的新思维方式和理念传递给父母</span></p></li></ul><ul cid=\"n24\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n25\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n26\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">幸福来自我自身的感受</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">不要在别人的想法，不要理会别人的嘲讽</span></p></li><li cid=\"n27\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n28\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">做事留三分余地，不能自己把自己逼到死角</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">不到万不得已不要一条路莽到黑，要给自己留有余地</span></p></li><li cid=\"n29\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n30\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">一个人是否愿意接受新的知识，在于是否有开放的心态，而非过去的教育程度</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">在一定的基础上要敢于尝试新的事物</span></p></li></ul><h3 cid=\"n31\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">向死而生 </span></h3><ul cid=\"n32\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n33\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n34\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">除死无大难</span></p></li><li cid=\"n35\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n36\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">活着就有希望</span></p></li></ul><ul cid=\"n38\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n39\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n40\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">我们并没有时间什么事情都做</span></p></li><li cid=\"n41\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n42\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">把要做的事情，从最重要的开始，列一个单子，然后从单子上最重要的事情开始做</span></p></li><li cid=\"n43\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n44\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">有些事情看似很重要，但是仔细想一想，其实也是可有可无，站在一生的角度看，放弃了也并无大碍</span></p></li></ul><h2 cid=\"n46\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">2.人生需要做减法（这个需要多看看）</span></h2><h3 cid=\"n47\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">没有选择的优势</span></h3><ul cid=\"n48\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n49\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n50\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">很多时候，我们把太多的 精力花在了选择上，而不是经营上，导致难以精进。或许少些选择，会更加聚焦，也会让我们更加幸福，更成功。</span></p></li></ul><h3 cid=\"n52\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">做人与作诗：我们需要林黛玉</span></h3><h3 cid=\"n53\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">西瓜和芝麻</span></h3><ul cid=\"n54\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n55\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n56\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"strong\"><strong><span md-inline=\"plain\">人一旦心志变得非常低，就很难提升自己、让自己走到越来越高的层次上。</span></strong></span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">要有凌云之志</span></p></li><li cid=\"n57\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n58\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">伪工作者 习惯于制作简单、重复且价值又低的工作，因为那种工作不需要太动脑筋，不会遇到非常大的困难。但是，人一旦习惯于这种工作，真正有创造性的工作就做不来了。</span></p></li><li cid=\"n59\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n60\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">思维方式和衡量价值的标准很重要</span></p></li><li cid=\"n61\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n62\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">除了眼光和思维方式的不同，捡西瓜更是要能力的，它不能靠运气，而是需要长期培养才能获得。</span></p></li></ul><p cid=\"n63\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><span md-inline=\"plain\">-** 如果致力于捡西瓜，就要耐得住寂寞**。</span></p><h3 cid=\"n65\" mdtype=\"heading\" style=\"break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; font-weight: bold; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><span md-inline=\"plain\">生也有涯，知也无涯</span></h3><ul cid=\"n66\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n67\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n68\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">做减法</span></p></li><li cid=\"n69\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n70\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">首先，能够跳出思维定式，换一个角度来判断一件事情的重要性；其次，敢于舍弃</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">换角度思考问题，要敢于舍弃</span></p></li><li cid=\"n71\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n72\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">思维定式：临时插进来的事情必须要做，不做就没礼貌，没面子，或者就亏了。其实那些事情如果不做，天根本不会塌下来。</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">先做最重要的，要避免这种思维</span></p></li><li cid=\"n73\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n74\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"strong\"><strong><span md-inline=\"plain\">要把一件事做好，首先想到的就应该是少做事情，而不是让自己更加忙碌</span></strong></span></p></li></ul><ul cid=\"n76\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n77\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n78\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">跳出思维定式有时需要大胆的反常规思考，甚至舍弃很多利益。</span></p></li><li cid=\"n79\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n80\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">要想换去更多的时间，就必须牺牲很多经济利益</span></p></li><li cid=\"n81\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n82\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">少做点事情就好，把要做的事减到最少</span></p></li></ul><ul cid=\"n84\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n85\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n86\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">人有一个弱点，就是见到小便宜想去占，很多利益舍不得放弃</span></p></li></ul><ul cid=\"n88\" mdtype=\"list\" style=\"margin-top: 0.8em; margin-bottom: 0.8em; margin-left: 0px; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\"><li cid=\"n89\" mdtype=\"list_item\" style=\"position: relative;\"><p cid=\"n90\" mdtype=\"paragraph\" style=\"line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">人生成功的秘诀在于做减法，而做减法的关键在于能够跳出一般人的思维定式，找到那些前期是无关紧要的事情，然后下决心把那些事情放弃掉。</span><span md-inline=\"softbreak\">\r\n</span><span md-inline=\"plain\">需要做减法，跳出思维定式需要从不同的角度看待问题，甚至反常规思考问题</span></p></li></ul><p><span style=\"font-size: x-large; font-weight: bold;\">视频</span></p><p><iframe onload=\"setIframeHeight(this)\" src=\"//player.bilibili.com/player.html?aid=980902831&amp;bvid=BV1z44y1G7Dv&amp;cid=584279833&amp;page=1\" scrolling=\"no\" border=\"0\" frameborder=\"no\" framespacing=\"0\" allowfullscreen=\"true\"> </iframe></p><p><br></p><p><br></p><p cid=\"n332\" mdtype=\"paragraph\" style=\"color: rgb(51, 51, 51); font-family: &quot;Open Sans&quot;, &quot;Clear Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px; line-height: inherit; orphans: 4; margin-top: 0.5rem; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span md-inline=\"plain\">                          </span></p>', 1651753520392, 1651753520392, 777, 0, 2, 1, '阅读,笔记', 1651753520392, 0, 2, 0);
INSERT INTO `ta_mbquestion` VALUES (1, '当你要外出一整天，你会', 'A 计划你要做什么和在什么时候做', 'B 说去就去', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (3, '你认为自己是一个                                   ', 'A   较为有条理的人                             ', 'B  较为随兴所至的人                           ', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (4, '假如你是一位老师，你会选教                             ', 'A 以事实为主的课程，                               ', 'B 涉及理论的课程                               ', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (5, '你通常                             ', 'A 与人容易混熟，                               ', 'B 比较沉静或矜持                               ', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (6, '一般来说，你和哪些人比较合得来？                   ', 'A 现实的人                 ', 'B 富于想象力的人', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (7, '你是否经常让', 'A 你的理智主宰你的情感', 'B 你的情感支配你的理智', 3, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (8, '处理许多事情上，你会喜欢', 'A 按照计划行事', 'B 凭兴所至行事', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (9, '你是否', 'A 容易让人了解，或是 ', 'B 难于让人了解', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (10, '按照程序表做事，', 'A 合你心意， ', 'B 令你感到束缚', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (11, '当你有一份特别的任务，你会喜欢', 'A 开始前小心组织计划， ', 'B 边做边找须做什么', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (12, '在大多数情况下，你会选择', 'A 按程序表做事', 'B 顺其自然', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (13, '大多数人会说你是一个', 'A 非常坦率开放的人', 'B 重视自我隐私的人', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (14, '你宁愿被人认为是一个', 'A 实事求是的人，', 'B 机灵的人', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (15, '在一大群人当中，通常是', 'A 你介绍大家认识，', 'B 别人介绍你', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (16, '你会跟哪些人做朋友？', 'A 脚踏实地的', 'B 常提出新创意的', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (17, '你倾向', 'A 重视逻辑多于感情', 'B 重视感情多于逻辑', 3, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (18, '你比较喜欢', 'A 很早就作计划', 'B 坐观事情发展才作计划', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (19, '你喜欢花很多的时间', 'A 合别人在一起', 'B 一个人独处', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (20, '与很多人一起会', 'A 令你活力培增，', 'B 常常令你心力憔悴', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (21, '你比较喜欢', 'A 很早便把', 'B 无拘无束，看当时有什么好玩就做什么', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (22, '计划一个旅程时，你较喜欢', 'A 事先知道大部分的日子会做什么', 'B 大部分的时间都是跟当天的感觉行事', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (23, '在社交聚会中，你', 'A 常常乐在其中', 'B 有时感到郁闷', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (24, '你通常', 'A 和别人容易混熟，', 'B 趋向自处一隅', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (25, '哪些人会更吸引你？', 'A 实事求是，具丰富常识的人', 'B 一个思想敏捷及非常聪颖的人', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (26, '在日常工作中，你会', 'A 通常预先计划，以免要在压力下工作', 'B 颇为喜欢处理迫使你分秒必争的突发工作', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (27, '你认为别人一般', 'A 用很短的时间便认识你', 'B 要花很长时间才认识你', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (28, '在下面两个词语中，哪一个词语更合你心意？', 'A 坦率开放	', 'B 注重隐私', 1, 0, 0);
