INSERT INTO `ta_mbquestion` VALUES (58, '在下面两个词语中，哪一个词语更合你心意？', 'A 具分析力', 'B 多愁善感', 3, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (59, '在下面两个词语中，哪一个词语更合你心意？', 'A 合情合理	', 'B 令人着迷', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (60, '当你要在一个星期内完成一个大项目，你在开始的时候会', 'A.把要做的不同工作依次列出，', 'B.马上动工', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (61, '在社交场合中，你经常会感到', 'A.与某些人很难打开话匣儿和保持对话，或是 ', 'B.很容易和别人交流', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (62, '要做许多人也做的事，你比较喜欢', 'A.按照一般认可的方法去做，', 'B.构想一个自己的想法', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (63, '你刚认识的朋友能否说出你的兴趣？', 'A.马上可以，', 'B.要待他们真正了解你之后才可以', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (64, '你通常较喜欢的科目是', 'A.讲授概念和原则的，', 'B.讲授事实和数据的', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (65, '哪个是较高的赞誉，或称许为？ ', 'A.一贯感性的人，', 'B.一贯理性的人', 3, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (66, '你认为按照程序表做事', 'A.有时是需要的，但一般来说你不大喜欢这样做，或是', 'B.大多数情况下是有帮助而且是你喜欢做的', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (67, '和一群人在一起，你通常会选', 'A.跟你很熟悉的个别人谈话，', 'B.参与大伙的谈话', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (68, '在社交聚会上，你会', 'A.是说话很多的一个，', 'B.让别人多说话', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (69, '把周末期间要完成的事列成清单，这个主意会', 'A.合你意，', 'B.使你提不起劲', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (70, '哪个是较高的赞誉，或行为?', 'A.能干的，', 'B.富有同情心', 3, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (71, '你通常喜欢', 'A.事先安排你的社交约会，', 'B.随兴之所至做事', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (72, '总的说来，要做一个大型作业时，你会选', 'A.边做边想该做什么，', 'B.首先把工作按步细分', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (73, '你能否滔滔不绝地与人聊天', 'A.只限于跟你有共同兴趣的人，', 'B.几乎跟任何人都可以', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (74, '你会', 'A.跟随一些证明有效的方法，或是', 'B.分析还有什么毛病，及针对尚未解决的难题', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (75, '为乐趣而阅读时，你会', 'A.喜欢奇特或创新的表达方式，', 'B.喜欢作者直话直说', 2, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (76, '你宁愿替哪一类上司（或者老师）工作？ ', 'A 天性淳良，但常常前后不一的，', 'B 言词尖锐但永远合乎逻辑的', 3, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (77, '你做事多数是', 'A 按当天心情去做，', 'B 照拟好的程序表去做', 4, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (78, '你是否', 'A 可以和任何人按需求从容地交谈  ', 'B 只是对某些人或在某种情况下才可以畅所欲言', 1, 0, 0);
INSERT INTO `ta_mbquestion` VALUES (79, '要作决定时，你认为比较重要的是', 'A 据事实衡量，', 'B 考虑他人的感受和意见', 3, 0, 0);
